/* Custom script
 ========================================================*/
$(document).ready(function () {
    $('.sf-menu').mobileMenu();

    
    var camera = $('#camera');
    var owl = $('#owl');
    var owl2 = $('#owl_2');
    var isotope = $('.isotope');

    if(camera.length > 0){
        camera.camera(
            {
                autoAdvance: false,
                height: '31.25%',
                minHeight: '200px',
                pagination: false,
                thumbnails: false,
                playPause: false,
                hover: false,
                loader: 'none',
                navigation: true,
                navigationHover: false,
                mobileNavHover: false,
                fx: 'simpleFade'
            }
        );
    }

    if(owl.length > 0){
        owl.owlCarousel(
            {
                navigation: true,
                autoPlay: true,
                slideSpeed: 300,
                stopOnHover: true,
                pagination: false,
                paginationSpeed: 400,
                singleItem: true,
                mouseDrag: false,
                navigationText: ["", ""]
            }
        );
    }

    if(owl2.length > 0){
        owl2.owlCarousel(
            {
                navigation: true,
                autoPlay: true,
                slideSpeed: 300,
                stopOnHover: true,
                pagination: false,
                paginationSpeed: 400,
                singleItem: true,
                mouseDrag: false,
                navigationText: ["", ""]
            }
        );
    }

    if(isotope.length > 0){
        isotope.isotope({
            itemSelector: '.element-item',
            layoutMode: 'fitRows'
        });

        $('#filters').on( 'click', 'a', function() {
            var filterValue = $( this ).attr('data-filter');
            console.log(filterValue);

            if(filterValue == '*'){
                isotope.isotope({ filter: filterValue });
            }else{
                isotope.isotope({ filter: '.'+filterValue });
            }
            return false;
        });
    }

});
